<?phpclass acco{	private $_reacco;	private $_revins;	private $_replat;public function __construct(array $donnees){	$this->hydrate($donnees);}public function hydrate(array $donnees){	foreach ($donnees as $key => $value)	{		$method = "set".ucfirst($key);		if (method_exists($this, $method))		{			$this->$method($value);		}	}}public function reacco(){return $this->_reacco;}public function revins(){return $this->_revins;}public function replat(){return $this->_replat;}public function setreacco($unreacco){$this->_reacco = $unreacco;}public function setrevins($unrevins){$this->_revins = $unrevins;}public function setreplat($unreplat){$this->_replat = $unreplat;}public function Devins($db){	$vins_dao = new vins_dao($db);	$unvins   = $vins_dao->get($this->_revins);	return $unvins->Devins();}public function Deplat($db){	$plat_dao = new plat_dao($db);	$unplat   = $plat_dao->get($this->_replat);	return $unplat->Deplat();}}?>