<?phpclass conc{	private $_reconc;	private $_deconc;public function __construct(array $donnees){	$this->hydrate($donnees);}public function hydrate(array $donnees){	foreach ($donnees as $key => $value)	{		$method = "set".ucfirst($key);		if (method_exists($this, $method))		{			$this->$method($value);		}	}}public function reconc(){return $this->_reconc;}public function deconc(){return $this->_deconc;}public function setreconc($unreconc){$this->_reconc = $unreconc;}public function setdeconc($undeconc){$this->_deconc = $undeconc;}}?>