<?phpclass degl{	private $_redegl;	private $_redegu;	private $_rebout;	private $_visuel;	private $_odorat;	private $_bouche;	private $_codegl;public function __construct(array $donnees){	$this->hydrate($donnees);}public function hydrate(array $donnees){	foreach ($donnees as $key => $value)	{		$method = "set".ucfirst($key);		if (method_exists($this, $method))		{			$this->$method($value);		}	}}public function redegl(){return $this->_redegl;}public function redegu(){return $this->_redegu;}public function rebout(){return $this->_rebout;}public function visuel(){return $this->_visuel;}public function odorat(){return $this->_odorat;}public function bouche(){return $this->_bouche;}public function codegl(){return $this->_codegl;}public function setredegl($unredegl){$this->_redegl = $unredegl;}public function setredegu($unredegu){$this->_redegu = $unredegu;}public function setrebout($unrebout){$this->_rebout = $unrebout;}public function setvisuel($unvisuel){$this->_visuel = $unvisuel;}public function setodorat($unodorat){$this->_odorat = $unodorat;}public function setbouche($unbouche){$this->_bouche = $unbouche;}public function setcodegl($uncodegl){$this->_codegl = $uncodegl;}public function Devins($db){	$bout_dao = new bout_dao($db);	$unbout   = $bout_dao->get($this->_rebout);;	return $unbout->Devins($db);}}?>