<?phpclass DEGU{	private $_redegu;	private $_nodegu;	private $_dadegu;public function __construct(array $donnees){	$this->hydrate($donnees);}public function hydrate(array $donnees){	foreach ($donnees as $key => $value)	{		$method = "set".ucfirst($key);		if (method_exists($this, $method))		{			$this->$method($value);		}	}}public function redegu(){return $this->_redegu;}public function nodegu(){return $this->_nodegu;}public function dadeg2(){return $this->_dadegu;}public function dadegu(){	if ($this->_dadegu <> 0)	{		$date = new DateTime($this->_dadegu);		$date = $date->format('d.m.Y');	}	else	{	$date = "";}	return $date;}public function setredegu($unredegu){$this->_redegu = $unredegu;}public function setnodegu($unnodegu){$this->_nodegu = $unnodegu;}public function setdadegu($undadegu){$this->_dadegu = $undadegu;}}?>