<?phpclass mvel{	private $_remvel;	private $_remven;	private $_rebout;	private $_nomvel;	private $_qtmvel;public function __construct(array $donnees){	$this->hydrate($donnees);}public function hydrate(array $donnees){	foreach ($donnees as $key => $value)	{		$method = "set".ucfirst($key);		if (method_exists($this, $method))		{			$this->$method($value);		}	}}public function remvel(){return $this->_remvel;}public function remven(){return $this->_remven;}public function rebout(){return $this->_rebout;}public function nomvel(){return $this->_nomvel;}public function qtmvel(){return $this->_qtmvel;}public function setremvel($unremvel){$this->_remvel = $unremvel;}public function setremven($unremven){$this->_remven = $unremven;}public function setrebout($unrebout){$this->_rebout = $unrebout;}public function setnomvel($unnomvel){$this->_nomvel = $unnomvel;}public function setqtmvel($unqtmvel){$this->_qtmvel = $unqtmvel;}public function Devins($db){	$bout_dao = new bout_dao($db);	$unbout   = $bout_dao->get($this->_rebout);;	return $unbout->Devins($db);}public function Damven($db){	$mven_dao = new mven_dao($db);	$unmven   = $mven_dao->get($this->_remven);;	return $unmven->Damven($db);}public function Defour($db){	$mven_dao = new mven_dao($db);	$unmven   = $mven_dao->get($this->_remven);;	return $unmven->Defour($db);}}?>