<?phpclass mvsl{	private $_remvsl;	private $_remvso;	private $_rebout;	private $_nomvsl;	private $_qtmvsl;	private $_reappr;public function __construct(array $donnees){	$this->hydrate($donnees);}public function hydrate(array $donnees){	foreach ($donnees as $key => $value)	{		$method = "set".ucfirst($key);		if (method_exists($this, $method))		{			$this->$method($value);		}	}}public function remvsl(){return $this->_remvsl;}public function remvso(){return $this->_remvso;}public function rebout(){return $this->_rebout;}public function nomvsl(){return $this->_nomvsl;}public function qtmvsl(){return $this->_qtmvsl;}public function reappr(){return $this->_reappr;}public function setremvsl($unremvsl){$this->_remvsl = $unremvsl;}public function setremvso($unremvso){$this->_remvso = $unremvso;}public function setrebout($unrebout){$this->_rebout = $unrebout;}public function setnomvsl($unnomvsl){$this->_nomvsl = $unnomvsl;}public function setqtmvsl($unqtmvsl){$this->_qtmvsl = $unqtmvsl;}public function setreappr($unreappr){$this->_reappr = $unreappr;}public function Devins($db){	$bout_dao = new bout_dao($db);	$unbout   = $bout_dao->get($this->_rebout);;	return $unbout->Devins($db);}public function Deappr($db){	$appr_dao = new appr_dao($db);	$unappr   = $appr_dao->get($this->_reappr);;	return $unappr->Deappr($db);}public function Damvso($db){	$mvso_dao = new mvso_dao($db);	$unmvso   = $mvso_dao->get($this->_remvso);;	return $unmvso->Damvso($db);}}?>