<?phpclass mvso{	private $_remvso;	private $_damvso;	private $_damvs2;	private $_nomvso;public function __construct(array $donnees){	$this->hydrate($donnees);}public function hydrate(array $donnees){	foreach ($donnees as $key => $value)	{		$method = "set".ucfirst($key);		if (method_exists($this, $method))		{			$this->$method($value);		}	}}public function remvso(){return $this->_remvso;}public function nomvso(){return $this->_nomvso;}public function damvs2(){return $this->_damvso;}public function damvso(){	if ($this->_damvso <> 0)	{		$date = new DateTime($this->_damvso);		$date = $date->format('d.m.Y');	}	else	{	$date = "";}	return $date;}public function setremvso($unremvso){$this->_remvso = $unremvso;}public function setdamvso($undamvso){$this->_damvso = $undamvso;}public function setdamvs2($undamvs2){$this->_damvso = $undamvs2;}public function setnomvso($unnomvso){$this->_nomvso = $unnomvso;}}?>