<?phpclass rece{	private $_rerece;	private $_derece;	private $_rerect;	private $_replat;public function __construct(array $donnees){	$this->hydrate($donnees);}public function hydrate(array $donnees){	foreach ($donnees as $key => $value)	{		$method = "set".ucfirst($key);		if (method_exists($this, $method))		{			$this->$method($value);		}	}}public function rerece(){return $this->_rerece;}public function derece(){return $this->_derece;}public function rerect(){return $this->_rerect;}public function replat(){return $this->_replat;}public function setrerece($unrerece){$this->_rerece = $unrerece;}public function setderece($underece){$this->_derece = $underece;}public function setrerect($unrerect){$this->_rerect = $unrerect;}public function setreplat($unreplat){$this->_replat = $unreplat;}public function Derect($db){	$rect_dao = new rect_dao($db);	$unrect   = $rect_dao->get($this->_rerect);	return $unrect->Derect();}public function Deplat($db){	$plat_dao = new plat_dao($db);	$unplat   = $plat_dao->get($this->_replat);	return $unplat->Deplat();}}?>