<?phpclass tbsd{	private $_codtab;	private $_retbsd;	private $_detbsd;public function __construct(array $donnees){	$this->hydrate($donnees);}public function hydrate(array $donnees){	foreach ($donnees as $key => $value)	{		$method = "set".ucfirst($key);		if (method_exists($this, $method))		{			$this->$method($value);		}	}}public function codtab(){return $this->_codtab;}public function retbsd(){return $this->_retbsd;}public function detbsd(){return $this->_detbsd;}public function setcodtab($uncodtab){$this->_codtab = $uncodtab;}public function setretbsd($unretbsd){$this->_retbsd = $unretbsd;}public function setdetbsd($undetbsd){$this->_detbsd = $undetbsd;}}?>