<?phpclass conc_dao{private $_db;public function __construct($db){	$this->setDb($db);}public function add(conc $conc){	$req = "INSERT INTO conc (deconc) VALUES ";	$req = $req.'("'.$conc->Deconc().'")';	echo $req;	$q = $this->_db->prepare($req);	$q->execute();}public function delete($reconc){$this->_db->exec("DELETE FROM conc WHERE reconc = ".$reconc);}public function get($reconc){	$reconc = (int) $reconc;	$q = $this->_db->query("SELECT * FROM conc WHERE reconc = ".$reconc);	$donnees = $q->fetch(PDO::FETCH_ASSOC);	return new conc($donnees);}public function getLast(){	$q = $this->_db->query("SELECT * FROM conc WHERE reconc = (SELECT MAX(reconc) FROM conc);");	$donnees = $q->fetch(PDO::FETCH_ASSOC);	return new conc($donnees);}public function getList(){	$conc_liste = array();	$q = $this->_db->query("SELECT * FROM conc");	while ($donnees = $q->fetch(PDO::FETCH_ASSOC))		{$conc_liste[] = new conc($donnees);}	return $conc_liste;}public function update(conc $conc){	$req = "UPDATE conc SET ";	$req = $req. "deconc = '".$conc->Deconc()."'";	$req = $req. " WHERE reconc = ".$conc->reconc();	$q = $this->_db->prepare($req);	$q->execute();}public function setDb(PDO $db){	$this->_db = $db;}}?>