<?phpclass degl_dao{private $_db;public function __construct($db){	$this->setDb($db);}public function add(degl $degl){	$req = "INSERT INTO degl (redegu,rebout,visuel,odorat,bouche,codegl) VALUES ";	$req = $req. '('.$degl->redegu().',';	$req = $req. $degl->rebout().',';	$req = $req. '"'.$degl->visuel().'",';	$req = $req. '"'.$degl->odorat().'",';	$req = $req. '"'.$degl->bouche().'",';	$req = $req. '"'.$degl->codegl().'")';		echo $req;	$q = $this->_db->prepare($req);	$q->execute();}public function delete($redegl){	$this->_db->exec("DELETE FROM degl WHERE redegl = ".$redegl);}public function get($redegl){	$redegl = (int) $redegl;	$q = $this->_db->query("SELECT * FROM degl WHERE redegl = ".$redegl);	$donnees = $q->fetch(PDO::FETCH_ASSOC);	return new degl($donnees);}public function getLast(){	$q = $this->_db->query("SELECT * FROM degl WHERE redegl = (SELECT MAX(redegl) FROM degl);");	$donnees = $q->fetch(PDO::FETCH_ASSOC);	return new degl($donnees);}public function getList(){	$degl_liste = array();	$q = $this->_db->query("SELECT * FROM degl");	while ($donnees = $q->fetch(PDO::FETCH_ASSOC))		{$degl_liste[] = new degl($donnees);}	return $degl_liste;}public function getListDegu($redegu){	$degl_liste = array();	$q = $this->_db->query('SELECT * FROM degl WHERE redegu = '.$redegu);	while ($donnees = $q->fetch(PDO::FETCH_ASSOC))	{		$degl_liste[] = new degl($donnees);	}	return $degl_liste;}public function getListBout($rebout){	$degl_liste = array();	$q = $this->_db->query('SELECT * FROM degl WHERE rebout = '.$rebout);	while ($donnees = $q->fetch(PDO::FETCH_ASSOC))	{		$degl_liste[] = new degl($donnees);	}	return $degl_liste;}public function update(degl $degl){	$req = "UPDATE appe SET ";	$req = $req. " WHERE redegl = ".$degl->redegl();	$q = $this->_db->prepare($req);	$q->execute();}public function setDb(PDO $db){	$this->_db = $db;}}?>