<?phpclass meda_dao{private $_db;public function __construct($db){	$this->setDb($db);}public function add(meda $meda){	$req = "INSERT INTO meda (revins,reconc,nimeda,period) VALUES ";	$req = $req.'('.$meda->Revins().','.$meda->Reconc().',"'.$meda->Nimeda().'","'.$meda->Period().'")';	echo $req;	$q = $this->_db->prepare($req);	$q->execute();}public function delete($remeda){	$this->_db->exec("DELETE FROM meda WHERE remeda = ".$remeda);}public function get($remeda){	$remeda = (int) $remeda;	$q = $this->_db->query("SELECT * FROM meda WHERE remeda = ".$remeda);	$donnees = $q->fetch(PDO::FETCH_ASSOC);	return new meda($donnees);}public function getLast(){	$q = $this->_db->query("SELECT * FROM meda WHERE remeda = (SELECT MAX(remeda) FROM meda);");	$donnees = $q->fetch(PDO::FETCH_ASSOC);	return new meda($donnees);}public function getList(){	$meda_liste = array();	$q = $this->_db->query("SELECT * FROM meda");	while ($donnees = $q->fetch(PDO::FETCH_ASSOC))		{$meda_liste[] = new meda($donnees);}	return $meda_liste;}public function getListVins($revins){	$meda_liste = array();	$q = $this->_db->query("SELECT * FROM meda WHERE revins = ".$revins);	while ($donnees = $q->fetch(PDO::FETCH_ASSOC))		{$meda_liste[] = new meda($donnees);}	return $meda_liste;}public function update(meda $meda){	$req = "UPDATE appe SET ";	$req = "revins = ".$meda->Revins();	$req = ", reconc = ".$meda->Reconc();	$req = ", nimeda = '".$meda->Nimeda()."'";	$req = ", nimeda = '".$meda->Period()."'";	$req = $req. " WHERE remeda = ".$meda->Remeda();	$q = $this->_db->prepare($req);	$q->execute();}public function setDb(PDO $db){	$this->_db = $db;}}?>