<?phpclass tbsd_dao{private $_db;public function __construct($db){	$this->setDb($db);}public function add(tbsd $tbsd){	$req = 'INSERT INTO tbsd (codtab,retbsd,detbsd) VALUES ';	$req = $req.'("'.$tbsd->Codtab().'","'.$tbsd->Retbsd().'","'.$tbsd->detbsd().'")';	echo $req;	$q = $this->_db->prepare($req);	$q->execute();}public function delete($codtab, $retbsd){	$this->_db->exec("DELETE FROM tbsd WHERE retbsd = '".$retbsd."' AND codtab = '".$codtab."'");}public function get($codtab, $retbsd){		$req = "SELECT * FROM tbsd WHERE retbsd = '".$retbsd."' AND codtab = '".$codtab."'";	$q = $this->_db->query($req);	$donnees = $q->fetch(PDO::FETCH_ASSOC);	return new tbsd($donnees);}public function getListEntiere(){	$tbsd_liste = array();	$q = $this->_db->query("SELECT * FROM tbsd");	while ($donnees = $q->fetch(PDO::FETCH_ASSOC))		{$tbsd_liste[] = new tbsd($donnees);}	return $tbsd_liste;}public function getList($codtab){	$tbsd_liste = array();	$q = $this->_db->query("SELECT * FROM tbsd WHERE codtab = '".$codtab."'");	while ($donnees = $q->fetch(PDO::FETCH_ASSOC))		{$tbsd_liste[] = new tbsd($donnees);}	return $tbsd_liste;}public function update(tbsd $tbsd){	$req = "UPDATE tbsd SET ";	$req = $req. " retbsd = '".$tbsd->Retbsd()."'";	$req = $req. ", codtab = '".$tbsd->Codtab()."'";	$req = $req. ", detbsd = '".$tbsd->Detbsd()."'";	$req = $req. " WHERE retbsd = '".$tbsd->Retbsd()."'";	$req = $req. " AND codtab = '".$tbsd->Codtab()."'";	echo $req;	$q = $this->_db->prepare($req);	$q->execute();}public function setDb(PDO $db){	$this->_db = $db;}}?>