<?phpclass tbse_dao{private $_db;public function __construct($db){	$this->setDb($db);}public function add(tbse $tbse){	$req = 'INSERT INTO tbse (codtab,detbse) VALUES ';	$req = $req.'("'.$tbse->Codtab().'","'.$tbse->Detbse().'")';	echo $req;	$q = $this->_db->prepare($req);	$q->execute();}public function delete($retbse){	$this->_db->exec("DELETE FROM tbse WHERE retbse = ".$retbse);}public function get($retbse){	$retbse = (int) $retbse;	$q = $this->_db->query("SELECT * FROM tbse WHERE retbse = ".$retbse);	$donnees = $q->fetch(PDO::FETCH_ASSOC);	return new tbse($donnees);}public function getLast(){	$q = $this->_db->query("SELECT * FROM tbse WHERE retbse = (SELECT MAX(retbse) FROM tbse);");	$donnees = $q->fetch(PDO::FETCH_ASSOC);	return new tbse($donnees);}public function getList(){	$tbse_liste = array();	$q = $this->_db->query('SELECT * FROM tbse');	while ($donnees = $q->fetch(PDO::FETCH_ASSOC))	{		$tbse_liste[] = new tbse($donnees);	}	return $tbse_liste;}public function update(tbse $tbse){	$req = "UPDATE tbse SET ";	$req = $req. "codtab = '".$tbse->Codtab()."', ";	$req = $req. "detbse = '".$tbse->Detbse()."'";	$req = $req. " WHERE retbse = ".$tbse->retbse();	$q = $this->_db->prepare($req);	$q->execute();}public function setDb(PDO $db){	$this->_db = $db;}}?>