<?phpclass meda{	private $_remeda;	private $_revins;	private $_reconc;	private $_nimeda;	private $_period;public function __construct(array $donnees){	$this->hydrate($donnees);}public function hydrate(array $donnees){	foreach ($donnees as $key => $value)	{		$method = "set".ucfirst($key);		if (method_exists($this, $method))		{			$this->$method($value);		}	}}public function remeda(){return $this->_remeda;}public function revins(){return $this->_revins;}public function reconc(){return $this->_reconc;}public function nimeda(){return $this->_nimeda;}public function period(){return $this->_period;}public function setremeda($unremeda){$this->_remeda = $unremeda;}public function setrevins($unrevins){$this->_revins = $unrevins;}public function setreconc($unreconc){$this->_reconc = $unreconc;}public function setnimeda($unnimeda){$this->_nimeda = $unnimeda;}public function setperiod($unperiod){$this->_period = $unperiod;}}?>