<?phpclass tari{	private $_retari;	private $_rebout;	private $_mtbout;	private $_datari;	private $_tytari;public function __construct(array $donnees){	$this->hydrate($donnees);}public function hydrate(array $donnees){	foreach ($donnees as $key => $value)	{		$method = "set".ucfirst($key);		if (method_exists($this, $method))		{			$this->$method($value);		}	}}public function retari(){return $this->_retari;}public function rebout(){return $this->_rebout;}public function mtbout(){return $this->_mtbout;}public function datari(){return $this->_datari;}public function tytari(){return $this->_tytari;}public function setretari($unretari){$this->_retari = $unretari;}public function setrebout($unrebout){$this->_rebout = $unrebout;}public function setmtbout($unmtbout){$this->_mtbout = $unmtbout;}public function setdatari($undatari){$this->_datari = $undatari;}public function settytari($untytari){$this->_tytari = $untytari;}}?>