<?phpclass tbse{	private $_retbse;	private $_codtab;	private $_detbse;public function __construct(array $donnees){	$this->hydrate($donnees);}public function hydrate(array $donnees){	foreach ($donnees as $key => $value)	{		$method = "set".ucfirst($key);		if (method_exists($this, $method))		{			$this->$method($value);		}	}}public function retbse(){return $this->_retbse;}public function codtab(){return $this->_codtab;}public function detbse(){return $this->_detbse;}public function setretbse($unretbse){$this->_retbse = $unretbse;}public function setcodtab($uncodtab){$this->_codtab = $uncodtab;}public function setdetbse($undetbse){$this->_detbse = $undetbse;}}?>